// Normalize payload from GoWA
const phone = ($json.from || $json.phone || "").toString().trim();
const text = ($json.text || $json.message || "").toString().toLowerCase();
const imageUrl = $json.image_url || $json.imageUrl || null;
const imageId = $json.image_id || $json.imageId || null;

return [{ json: { phone, text, image_url: imageUrl, image_id: imageId } }];

// Detect intent from text
const text = $json.text || "";
const lowerText = text.toLowerCase();

// Intent OFF keywords
const offKeywords = [
  'jangan ingat',
  'jangan diingatkan',
  'stop reminder',
  'matikan reminder',
  'jangan reminder'
];

// Intent ON keywords
const onKeywords = [
  'nyalakan reminder',
  'aktifkan reminder',
  'reminder on'
];

// Intent ASK keywords
const askKeywords = [
  'kok diingetin',
  'kenapa diingatkan',
  'reminder apa',
  'cara matiin reminder',
  'gimana stop'
];

// PDF Report keywords
const pdfKeywords = [
  'buat pdf',
  'pdf pengeluaran',
  'laporan pdf',
  'export pdf',
  'unduh pdf',
  'download pdf',
  'pdf bulan ini',
  'pdf bulan lalu',
  'pdf setahun',
  'pdf tahun ini'
];

// Transaction type confirmation keywords
const incomeKeywords = ['pemasukan', 'income', 'masuk', 'terima', 'dapat'];
const expenseKeywords = ['pengeluaran', 'expense', 'keluar', 'bayar', 'beli'];

// Style keywords
const styleMap = {
  'gaya santai': 'santai',
  'gaya biasa': 'netral',
  'gaya netral': 'netral',
  'gaya formal': 'formal',
  'gaya gaul': 'gaul'
};

let intent = 'normal';
let style = null;

for (const key of Object.keys(styleMap)) {
  if (lowerText.includes(key)) {
    intent = 'set_style';
    style = styleMap[key];
    break;
  }
}

// Check if image upload
if ($json.image_url || $json.image_id) {
  intent = 'image_upload';
} else if (intent === 'normal') {
  // Check transaction type confirmation
  if (incomeKeywords.some(kw => lowerText.includes(kw))) {
    intent = 'confirm_income';
  } else if (expenseKeywords.some(kw => lowerText.includes(kw))) {
    intent = 'confirm_expense';
  } else if (pdfKeywords.some(kw => lowerText.includes(kw))) {
    intent = 'generate_pdf_expense';
  } else if (offKeywords.some(kw => lowerText.includes(kw))) {
    intent = 'reminder_off';
  } else if (onKeywords.some(kw => lowerText.includes(kw))) {
    intent = 'reminder_on';
  } else if (askKeywords.some(kw => lowerText.includes(kw))) {
    intent = 'ask_reminder';
  }
}

return [{ json: { ...$json, intent, style } }];

// Extract period from text
const text = ($json.text || "").toLowerCase();
let period = 'this_month'; // default

if (text.includes('bulan lalu') || text.includes('bulan kemarin')) {
  period = 'last_month';
} else if (text.includes('setahun') || text.includes('tahun ini') || text.includes('sepanjang tahun')) {
  period = 'this_year';
} else if (text.includes('bulan ini') || text.includes('bulan sekarang')) {
  period = 'this_month';
}

return [{ json: { ...$json, period } }];

// Handle PDF response - check if success or error
const response = $json.body || $json;

if (response.success === true) {
  // Success: ada PDF
  return [{
    json: {
      ...$json,
      pdf_success: true,
      pdf_base64: response.data.pdf_base64,
      filename: response.data.filename,
      period_label: response.data.period_label,
      total_expense: response.data.total_expense,
      transaction_count: response.data.transaction_count
    }
  }];
} else {
  // Error: plan tidak support atau subscription expired
  return [{
    json: {
      ...$json,
      pdf_success: false,
      error_message: response.message || 'Error generating PDF',
      error_data: response.data || {},
      response_style: response.data?.response_style || 'santai'
    }
  }];
}

// Generate error message sesuai response_style
const style = $json.response_style || 'santai';
const errorData = $json.error_data || {};

const messages = {
  santai: 'Wah, fitur PDF report ini cuma buat plan Pro/VIP nih. Upgrade dulu yuk!',
  netral: 'Fitur PDF report hanya tersedia untuk plan Pro dan VIP. Silakan upgrade plan Anda.',
  formal: 'Maaf, fitur laporan PDF hanya dapat diakses oleh pengguna plan Pro dan VIP. Mohon upgrade plan terlebih dahulu.',
  gaul: 'Eits, PDF report cuma buat plan Pro/VIP aja nih. Upgrade dulu dong!'
};

const message = messages[style] || messages.netral;

return [{ json: { ...$json, replyMessage: message } }];

