={{ JSON.stringify({
  to: $json.phone,
  message: `Laporan PDF pengeluaran ${$json.period_label} sudah siap! Total: Rp ${String($json.total_expense || 0).replace(/\B(?=(\d{3})+(?!\d))/g, '.')} (${$json.transaction_count || 0} transaksi)`,
  file: $json.pdf_base64,
  filename: $json.filename
}) }}

