// Generate question sesuai response_style
const style = ($json.data?.response_style || $json.response_style || 'santai').toLowerCase();

const questions = {
  santai: [
    'Wah, dapat gambar nih! Ini pemasukan atau pengeluaran?',
    'Oke gambar udah diterima. Ini masuk atau keluar?',
    'Gambar sudah sampai! Ini pemasukan atau pengeluaran ya?'
  ],
  netral: [
    'Gambar diterima. Apakah ini pemasukan atau pengeluaran?',
    'Gambar sudah diterima. Silakan konfirmasi: pemasukan atau pengeluaran?',
    'Gambar berhasil diterima. Ini pemasukan atau pengeluaran?'
  ],
  formal: [
    'Gambar telah diterima. Mohon konfirmasi: pemasukan atau pengeluaran?',
    'Gambar berhasil diterima. Silakan tentukan jenis transaksi: pemasukan atau pengeluaran?',
    'Gambar telah kami terima. Mohon konfirmasi jenis transaksi: pemasukan atau pengeluaran?'
  ],
  gaul: [
    'Dapet gambar nih! Ini masuk atau keluar?',
    'Gambar udah sampe! Ini pemasukan atau pengeluaran?',
    'Oke gambar masuk! Ini masuk atau keluar ya?'
  ]
};

const pool = questions[style] || questions.santai;
const message = pool[Math.floor(Math.random() * pool.length)];
const delay = Math.floor(Math.random() * 2000) + 1000;

return [{ json: { ...$json, replyMessage: message, delay } }];

// Generate confirmation message sesuai response_style
const style = ($json.data?.response_style || $json.response_style || 'santai').toLowerCase();
const transactionType = $json.data?.transaction_type || 'expense';
const amount = $json.data?.amount || null;
const typeLabel = transactionType === 'income' ? 'pemasukan' : 'pengeluaran';

const messages = {
  santai: [
    `Oke, transaksi ${typeLabel} sudah dicatat!${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Done! ${typeLabel} sudah masuk ke catatan.${amount ? ' Total: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Siap! ${typeLabel} sudah tersimpan.${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`
  ],
  netral: [
    `Transaksi ${typeLabel} berhasil dicatat.${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Transaksi ${typeLabel} telah disimpan.${amount ? ' Total: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Transaksi ${typeLabel} berhasil ditambahkan.${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`
  ],
  formal: [
    `Transaksi ${typeLabel} telah berhasil dicatat.${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Transaksi ${typeLabel} telah disimpan ke dalam catatan.${amount ? ' Total: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Transaksi ${typeLabel} berhasil ditambahkan ke sistem.${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`
  ],
  gaul: [
    `Oke, ${typeLabel} udah masuk!${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Done! ${typeLabel} tersimpan.${amount ? ' Total: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`,
    `Siap! ${typeLabel} masuk ke catatan.${amount ? ' Jumlah: Rp ' + String(amount).replace(/\B(?=(\d{3})+(?!\d))/g, '.') : ''}`
  ]
};

const pool = messages[style] || messages.santai;
const message = pool[Math.floor(Math.random() * pool.length)];
const delay = Math.floor(Math.random() * 2000) + 1000;

return [{ json: { ...$json, replyMessage: message, delay } }];

// Template pools for responses
const styleLabel = ($json.style || '').toString();
const templates = {
  reminder_off: [
    'Baik, reminder sudah dimatikan. Ketik "nyalakan reminder" jika ingin mengaktifkan lagi.',
    'Reminder sudah dinonaktifkan. Kamu tidak akan diingatkan lagi.',
    'Oke, reminder dimatikan. Untuk mengaktifkan lagi, ketik "reminder on".'
  ],
  reminder_on: [
    'Reminder sudah diaktifkan! Kamu akan diingatkan setiap hari jika belum input transaksi.',
    'Baik, reminder aktif. Saya akan mengingatkan kamu setiap hari.',
    'Reminder sudah nyala. Kamu akan mendapat notifikasi harian.'
  ],
  ask_reminder: [
    'Reminder adalah notifikasi harian untuk mengingatkan kamu mencatat transaksi. Ketik "jangan ingat" untuk mematikan, atau "reminder on" untuk mengaktifkan.',
    'Saya akan mengingatkan kamu setiap hari jika belum input transaksi. Untuk mematikan: ketik "jangan diingatkan".',
    'Reminder membantu kamu tidak lupa mencatat keuangan. Matikan dengan "stop reminder" atau aktifkan dengan "reminder on".'
  ],
  set_style: [
    `Gaya balasan diatur ke ${styleLabel}.`,
    `Siap! Sekarang gaya balasan ${styleLabel}.`,
    `Done, gaya balasan diganti ke ${styleLabel}.`
  ],
  normal: [
    'Halo! Saya bot pencatat keuangan. Ketik "reminder apa" untuk info lebih lanjut.',
    'Hai! Saya siap membantu mencatat keuangan kamu. Ketik "reminder apa" untuk info reminder.',
    'Selamat datang! Gunakan saya untuk mencatat transaksi. Ketik "reminder apa" untuk info.'
  ]
};

const intent = $json.intent || 'normal';
const pool = templates[intent] || templates.normal;
const message = pool[Math.floor(Math.random() * pool.length)];

// Random delay 1-3 seconds
const delay = Math.floor(Math.random() * 2000) + 1000;

return [{ json: { ...$json, replyMessage: message, delay } }];
